from langflow.custom.custom_component.component import Component
from langflow.io import MessageInput, Output
from langflow.schema.message import Message


class ParallelUppercaseComponent(Component):
    display_name = "Parallel: Uppercase"
    description = "Branch 1: Converts text to uppercase"
    icon = "arrow-up"
    name = "ParallelUppercaseComponent"

    inputs = [
        MessageInput(
            name="input_message",
            display_name="Input Message",
            info="Message to process",
        ),
    ]

    outputs = [
        Output(display_name="Output", name="output", method="build_output"),
    ]

    def build_output(self) -> Message:
        input_text = self.input_message.text if hasattr(self.input_message, 'text') else str(self.input_message)
        
        # Process: Convert to uppercase
        result = input_text.upper()
        
        # Preserve metadata and add branch info
        metadata = self.input_message.metadata.copy() if hasattr(self.input_message, 'metadata') and self.input_message.metadata else {}
        metadata.update({
            "branch": "uppercase",
            "uppercase_result": result
        })
        
        self.status = f"‚úì Branch 1 (Uppercase): '{input_text}' ‚Üí '{result}'"
        
        return Message(
            text=result,
            metadata=metadata
        )

from langflow.custom.custom_component.component import Component
from langflow.io import MessageInput, Output
from langflow.schema.message import Message


class ParallelReverseComponent(Component):
    display_name = "Parallel: Reverse"
    description = "Branch 2: Reverses the text"
    icon = "rotate-ccw"
    name = "ParallelReverseComponent"

    inputs = [
        MessageInput(
            name="input_message",
            display_name="Input Message",
            info="Message to process",
        ),
    ]

    outputs = [
        Output(display_name="Output", name="output", method="build_output"),
    ]

    def build_output(self) -> Message:
        input_text = self.input_message.text if hasattr(self.input_message, 'text') else str(self.input_message)
        
        # Process: Reverse the text
        result = input_text[::-1]
        
        # Preserve metadata and add branch info
        metadata = self.input_message.metadata.copy() if hasattr(self.input_message, 'metadata') and self.input_message.metadata else {}
        metadata.update({
            "branch": "reverse",
            "reverse_result": result
        })
        
        self.status = f"‚úì Branch 2 (Reverse): '{input_text}' ‚Üí '{result}'"
        
        return Message(
            text=result,
            metadata=metadata
        )
