from langflow.custom.custom_component.component import Component
from langflow.io import MessageTextInput, IntInput, MessageInput, Output
from langflow.schema.message import Message


class LengthConditionalRouter(Component):
    display_name = "Length If-Else"
    description = "Routes input based on text length comparison with threshold."
    icon = "split"
    name = "LengthConditionalRouter"

    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)
        self.__iteration_updated = False

    inputs = [
        MessageTextInput(
            name="input_text",
            display_name="Text Input",
            info="The text to evaluate.",
            required=True,
        ),
        IntInput(
            name="threshold",
            display_name="Length Threshold",
            info="The character count threshold for comparison.",
            value=10,
        ),
        MessageInput(
            name="true_case_message",
            display_name="Case True (>= threshold)",
            info="The message to pass if length >= threshold.",
            advanced=True,
        ),
        MessageInput(
            name="false_case_message",
            display_name="Case False (< threshold)",
            info="The message to pass if length < threshold.",
            advanced=True,
        ),
        IntInput(
            name="max_iterations",
            display_name="Max Iterations",
            info="The maximum number of iterations for the conditional router.",
            value=10,
            advanced=True,
        ),
    ]

    outputs = [
        Output(display_name="True (>= threshold)", name="true_result", method="true_response", group_outputs=True),
        Output(display_name="False (< threshold)", name="false_result", method="false_response", group_outputs=True),
    ]

    def _pre_run_setup(self):
        self.__iteration_updated = False

    def evaluate_condition(self, input_text: str, threshold: int) -> bool:
        """Returns True if text length >= threshold, False otherwise."""
        return len(input_text) >= threshold

    def iterate_and_stop_once(self, route_to_stop: str):
        """Handles iteration counting and stops the specified route."""
        if not self.__iteration_updated:
            # Increment iteration counter
            current_iteration = self.ctx.get(f"{self._id}_iteration", 0)
            self.update_ctx({f"{self._id}_iteration": current_iteration + 1})
            self.__iteration_updated = True

            # Stop the route that shouldn't execute
            self.stop(route_to_stop)

    def true_response(self) -> Message:
        """Executes when condition is TRUE (length >= threshold)."""
        result = self.evaluate_condition(self.input_text, self.threshold)

        if result:
            # Condition is TRUE - return the true case message
            self.status = f"✓ TRUE: length {len(self.input_text)} >= {self.threshold}"
            self.iterate_and_stop_once("false_result")
            
            # Pass through the true case message or create default
            if self.true_case_message:
                return self.true_case_message
            else:
                return Message(
                    text=self.input_text,
                    metadata={
                        "condition": "true",
                        "length": len(self.input_text),
                        "threshold": self.threshold
                    }
                )
        else:
            # Condition is FALSE - stop this branch
            self.iterate_and_stop_once("true_result")
            return Message(text="")

    def false_response(self) -> Message:
        """Executes when condition is FALSE (length < threshold)."""
        result = self.evaluate_condition(self.input_text, self.threshold)

        if not result:
            # Condition is FALSE - return the false case message
            self.status = f"✓ FALSE: length {len(self.input_text)} < {self.threshold}"
            self.iterate_and_stop_once("true_result")
            
            # Pass through the false case message or create default
            if self.false_case_message:
                return self.false_case_message
            else:
                return Message(
                    text=self.input_text,
                    metadata={
                        "condition": "false",
                        "length": len(self.input_text),
                        "threshold": self.threshold
                    }
                )
        else:
            # Condition is TRUE - stop this branch
            self.iterate_and_stop_once("false_result")
            return Message(text="")
